for (a = document.querySelectorAll("[href]"), ext = ".php", String.prototype.insertAt = function (t, e) {
		return this.substr(0, t) + e + this.substr(t)
	}, i = 0; i < a.length; i++)
	if (value = a[i].getAttribute("href"), -1 == value.indexOf(".") && -1 == value.indexOf("?") && -1 == value.indexOf("#")) a[i].href += ext;
	else if (value.indexOf("?") > -1 && -1 == value.indexOf(".")) {
	var inat = value.indexOf("?")
		, pstring = a[i].getAttribute("href");
	alert(pstring);
	var nstring = pstring.insertAt(inat, ext);
	a[i].setAttribute("href", nstring)
}
else if (value.indexOf("#") > -1 && -1 == value.indexOf(".")) {
	var inat = value.indexOf("#")
		, pstring = a[i].getAttribute("href");
	alert(pstring);
	var nstring = pstring.insertAt(inat, ext);
	a[i].setAttribute("href", nstring)
}